import React, { useState } from "react";
import ViewMovie from "./ViewMovie"; // Component to show movie details

const MovieList = ({ movies }) => {
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Handle movie click to select a movie
  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  return (
    <div>
      {selectedMovie ? (
        // If a movie is selected, show movie details
        <ViewMovie movie={selectedMovie} />
      ) : (
        // Else, show the list of movies
        <div className="movie-list">
          {movies.map((movie, index) => (
            <div
              key={index}
              onClick={() => handleMovieClick(movie)} // Update selected movie
              className="movie-item cursor-pointer"
            >
              <img src={movie.Poster} alt={movie.Title} />
              <h3>{movie.Title}</h3>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MovieList;
