import Header from "./components/Header";
import MovieList from "./components/MovieList";
import React, { useState, useEffect } from "react";
import Search from "./components/Search";
import Error from "./components/Error";

function App() {
  // useState
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState("Batman");
  const [pagination, setPagination] = useState(1);
  const [error, setError] = useState("");

  const DefaultMovies = async () => {
    const url = `http://www.omdbapi.com/?s=${search}&apikey=8c9ec521&page=2`; // Use search dynamically
    const response = await fetch(url);
    const responseToJson = await response.json();
  
    if (responseToJson.Response === "True") {
      setMovies(responseToJson.Search);
      setError(""); // Clear error when movies are found
    } else {
      setMovies([]); // Clear movies if no results
      setError("No Movies Found"); // Set error message
    }
  };
  
  useEffect(() => {
    DefaultMovies(); // Fetch movies when component mounts
  }, [search]); // Run when `search` changes

  return (
    <div className="App">
      <Header />
      <Search search={search} setSearch={setSearch} />
      <p className="text-center text-red-500">{error}</p>
      <div className="2xl bg-blue-700 mt-10">
        <div className="sm:container sm:mx-auto mx-2 bg-green-700">
          <MovieList movies={movies} />
        </div>
      </div>
    </div>
  );
}
export default App;
