import Header from "./components/Header";
import MovieList from "./components/MovieList";
import React, { useState, useEffect } from "react";
import Search from "./components/Search";
import ButtonPrevious from "./components/ButtonPrevious";
import ButtonNext from './components/ButtonNext';
import PaginationNumbers from "./components/PaginationNumbers";

function App() {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState("Batman");
  const [currentPage, setCurrentPage] = useState(1);
  const [error, setError] = useState("");

  const fetchMovies = async () => {
    const url = `http://www.omdbapi.com/?s=${search}&apikey=8c9ec521&page=${currentPage}`;
    const response = await fetch(url);
    const responseToJson = await response.json();

    if (responseToJson.Response === "True") {
      setMovies(responseToJson.Search);
      setError("");
    } else {
      setMovies([]);
      setError("No Movies Found");
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [search, currentPage]); // Fetch when `search` or `pagination` changes


  return (
    <div className="App">
      <Header />
      <Search search={search} setSearch={setSearch} />
      <p className="text-center text-red-500">{error}</p>
      <div className="2xl bg-blue-700 mt-10">
        <div className="sm:container sm:mx-auto mx-2 bg-green-700">
          <MovieList movies={movies} />
        </div>
        <div className="flex justify-center gap-5 items-center mt-4 w-full">
          <ButtonPrevious currentPage = {currentPage} />
          <PaginationNumbers currentPage = {currentPage} />
          <ButtonNext currentPage = {currentPage} />
        </div>
      </div>
    </div>
  );
}

export default App;
